mapper.select:
SELECT *
FROM sys_commodity
WHERE comEachPrice >= #{min}
AND comEachPrice <= #{max}

SELECT *
FROM sys_commodity
WHERE comQuantityNow >= #{min}
AND comQuantityNow <= #{max}

SELECT *
FROM sys_commodity
WHERE 1=1

    AND comId like concat('%', '${comId}', '%')


    AND comName like concat('%', '${comName}', '%')

AND comEachPrice >= #{minPrice}
AND comEachPrice <= #{maxPrice}
AND comQuantityNow >= #{minQuantity}
AND comQuantityNow <= #{maxQuantity}

    AND comDescription like concat('%', '${comDescription}', '%')
